mapper.select:
SELECT
       COUNT(*)
  FROM MESSAGES
 WHERE MEM_CODE = #{ memberNo }
   AND BOX_TYPE = #{ messageboxNo }
   AND IS_DELETED = 'N'

SELECT
       REGI_DATE
  FROM MEMBER
  JOIN FUNDRAISER ON (MEMBER.MEM_CODE = FUNDRAISER.RAISER_CODE)
 WHERE MEMBER.NICKNAME = #{ nickname }
    OR FUNDRAISER.RAI_NICKNAME = #{ nickname }

SELECT
       MEM_CODE
  FROM MEMBER
  JOIN FUNDRAISER ON (MEMBER.MEM_CODE = FUNDRAISER.RAISER_CODE)
 WHERE MEMBER.NICKNAME = #{ nickname }
    OR FUNDRAISER.RAI_NICKNAME = #{ nickname }

mapper.update:
UPDATE MESSAGES
   SET IS_READ = 'Y'
 WHERE MESSAGE_CODE = #{ messageNo }

UPDATE MESSAGES
   SET IS_DELETED = 'Y'
 WHERE MESSAGE_CODE = #{ messageNo }
