mapper.select:
SELECT A.RNUM , A.NOTICE_NO , A.NOTICE_TITLE , A.NOTICE_CONTENT , A.UPLOAD_DATE , A.READ_COUNT , A.MEMBER_CODE , A.NICKNAME FROM (SELECT ROWNUM RNUM , B.NOTICE_NO , B.NOTICE_TITLE , B.NOTICE_CONTENT , B.UPLOAD_DATE , B.READ_COUNT , B.MEMBER_CODE , B.NICKNAME FROM (SELECT C.NOTICE_NO , C.NOTICE_TITLE , C.NOTICE_CONTENT , C.UPLOAD_DATE , C.READ_COUNT , C.MEMBER_CODE , E.NICKNAME FROM NOTICE C JOIN MEMBER E ON(C.MEMBER_CODE = E.MEM_CODE) ORDER BY C.NOTICE_NO DESC) B
WHERE ROWNUM <= #{ endRow }
) A
A.RNUM >= #{ startRow }
AND A.NOTICE_TITLE LIKE '%' || #{ searchValue } || '%'
AND A.NICKNAME LIKE '%' || #{ searchValue } || '%'

mapper.update:
UPDATE
      NOTICE A
SET   A.NOTICE_TITLE = #{ noticeTitle }
    , A.NOTICE_CONTENT = #{ noticeContent }
WHERE A.NOTICE_NO = #{ no }

mapper.delete:
DELETE
FROM   NOTICE A
WHERE  A.NOTICE_NO = #{ no }

mapper.update:
UPDATE
       NOTICE A
SET A.READ_COUNT = (SELECT B.READ_COUNT
                      FROM  NOTICE B
                      WHERE B.NOTICE_NO = #{ no }
                     ) + 1
WHERE A.NOTICE_NO = #{ no }
