     , A.MAINIMG
     , A.TEL
     , A.PJ_EMAIL
     , A.REFUNDRULE
     , A.VIDEO_URL
  )
VALUES
    (
    SEQ_PROJECT_NO.NEXTVAL
        , #{ raiserCode }
        , #{ description }
        , #{ fundGoal }
        , DEFAULT
        , #{ startDate }
